import React, { useState } from 'react';
import { IoMdSearch } from 'react-icons/io';

const ImageHover = (props) => {
	const { setIsModalOpen, work, i, setIndex, setIndexNumber } = props;
	const [magnifyShow, setMagnifyShow] = useState(false);
	return (
		<>
			<img
				src={work}
				data={i}
				alt="project-screenshot"
				className="grid-img"
				onMouseEnter={() => setMagnifyShow(true)}
				onMouseLeave={() => setMagnifyShow(false)}
				onClick={() => {
					setIsModalOpen((prev) => !prev);
					setIndex();
					setIndexNumber(i);
				}}
			/>
			{magnifyShow && <IoMdSearch />}
		</>
	);
};

export default ImageHover;
