import React, { useState, useMemo } from 'react';
import images from './images';
import ModalComponent from './ModalComponent';
import UseModal from './UseModal';
import './work.css';
import ImageGrid from './ImageGrid';
import ModalContentFunctions from './ModalContentFunctions';

const Work = () => {
	const [Modal, isModalOpen, setIsModalOpen] = UseModal();
	const firstImages = [];
	for (let i = 0; i < images.length; i++) {
		firstImages.push(images[i][0]);
	}
	const [slidesState, setSlide] = useState(null);
	let [arrIdx, setArrIdx] = useState(0);
	let [workIndex, setWorkIndex] = useState(2);
	const toggleModalOpen = () => setIsModalOpen((prev) => !prev);
	useEffect(() => {
		const setIndex = (index) => {
			console.log(index);
			setSlide(images[index][arrIdx]);
		};
		const forwardSlide = () => {
			setArrIdx(arrIdx++);
			setSlide(images[workIndex][arrIdx]);
		};
		const backSlide = () => {
			setArrIdx(arrIdx--);
			setSlide(images[workIndex][arrIdx]);
		};
	});
	return (
		<div className="work-div">
			<ImageGrid
				firstImages={firstImages}
				slidesState={slidesState}
				setIsModalOpen={setIsModalOpen}
				setIndex={setIndex}
			/>
			<ModalComponent
				setIsModalOpen={setIsModalOpen}
				isModalOpen={isModalOpen}
				Modal={Modal}
				toggleModalOpen={toggleModalOpen}
			>
				<ModalContentFunctions
					toggleModalOpen={toggleModalOpen}
					slidesState={slidesState}
					backSlide={backSlide}
					forwardSlide={forwardSlide}
				/>
			</ModalComponent>
		</div>
	);
};

export default Work;
