import React from 'react';
import './Contact.css';

class Contact extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			name: '',
			email: '',
			message: '',
		};
	}
	render() {
		return (
			<div class="content content-contact">
				<div class="contact-me">
					<h3>get in touch</h3>
				</div>
				<div className="form-div">
					<form
						className="form-contact"
						action="action_page.php"
						onSubmit={this.handleSubmit.bind(this)}
						method="POST"
					>
						<label htmlFor="name" className="visually-hidden">
							Name:
						</label>
						<input
							type="text"
							id="name"
							name="name"
							placeholder="name"
							value={this.state.name}
							onChange={this.onNameChange.bind(this)}
						/>
						<label for="email" className="visually-hidden">
							Email:
						</label>
						<input
							type="email"
							id="email"
							name="email"
							placeholder="email"
						/>
						<label for="subject" className="visually-hidden">
							Subject:
						</label>
						<input
							type="text"
							id="subject"
							name="subject"
							placeholder="subject"
						/>
						<label for="message" className="visually-hidden">
							Write your Message:
						</label>
						<textarea
							id="message"
							name="message"
							placeholder="message"
						></textarea>
						<label for="submit" className="visually-hidden">
							Send your Message:
						</label>
						<input type="submit" value="send" id="submit" />
					</form>
				</div>
			</div>
		);
	}
}

export default Contact;

