import React, { useState } from 'react';
import images from './images';
import './work.css';

const Work = (props) => {
	const [slidesState, setNextSlide] = useState({
		firstSlide: [
			{ egypt: images.egypt[0] },
			{ greco: images.greco[0] },
			{ map: images.map[0] },
			{ news: images.news[0] },
			{ weather: images.weather[0] },
		],
	});
	const nextSlide = () => {
		setNextSlide({
			firstSlide: [
				{ egypt: images.egypt[0] },
				{ greco: images.greco[0] },
				{ map: images.map[0] },
				{ news: images.news[0] },
				{ weather: images.weather[0] },
			],
		});
	};
	return (
		<div id="modal-slides" className="modal">
			<div className="modal-content">
				<div className="modal-container">
					<span className="button-display-topright">&times;</span>
					<div className="slides-content">
						<button className="display-left">&#10094;</button>
						<button className="display-right">&#10095;</button>
					</div>
				</div>
			</div>
		</div>
	);
};

export default Work;
