import React from 'react';
import { GiMagnifyingGlass } from 'react-icons/gi';

const WorkGrid = (props) => {
	const [isShown, setIsShown] = useState(false);
	const { slidesState } = this.props;
	let WorkItem = slidesState.firstImages.map((work, i) => (
		<div className="grid-image-div" key={`${work}_${i}`}>
			<img
				src={work}
				alt="project-screenshot"
				className="grid-img"
				onMouseEnter={() => magnifyIsShown(true)}
				onMouseLeave={() => magnifyIsShown(false)}
			/>
			<GiMagnifyingGlass />
		</div>
	));
	return <>{WorkItem}</>;
};

export default WorkGrid;
