// import React from 'react';
import React, { useState } from 'react';
import images from './images';
import ModalComponent from './ModalComponent';
import UseModal from './UseModal';
import './work.css';
import WorkGrid from './WorkGrid.js';

const Work = () => {
	const [Modal, isModalOpen, setIsModalOpen] = UseModal();
	const toggleModalOpen = () => setIsModalOpen((prev) => !prev);
	const firstImages = [];
	for (let i = 0; i < images.length; i++) {
		firstImages.push(images[i][0]);
	}
	const [slidesState, setNextSlide] = useState('');
	return (
		<div className="work-div">
			{/* <WorkGrid
				firstImages={firstImages}
				slidesState={slidesState}
				setNextSlide={setNextSlide}
				setIsModalOpen={setIsModalOpen}
				slideHolder={slideHolder}
			/> */}
			{firstImages.map((work, i) => {
				return (
					<article className="article">
						<section className="featuredImage">
							<img src={article.image} alt="" />
						</section>
						<section className="articleContent">
							<button id="43">
								<h3>{article.title}</h3>
							</button>
							<h6 className="tags">{article.tags}</h6>
						</section>
						<section className="impressions">
							{article.impressions}
						</section>
						<div className="clearfix"></div>
					</article>
				);
			})}
			<ModalComponent
				setIsModalOpen={setIsModalOpen}
				isModalOpen={isModalOpen}
				Modal={Modal}
				toggleModalOpen={toggleModalOpen}
				size="large"
			>
				<div className="modal-container">
					<span
						className="button-display-topright"
						role="button"
						aria-label="Close Button"
						tabIndex={0}
						onClick={toggleModalOpen}
						onKeyPress={(e) => {
							if (e.key === 'Enter') {
								toggleModalOpen();
							}
						}}
					>
						&times;
					</span>
					<div className="slides-content">
						<div className="slides-div">
							<img
								src={firstImages[0]}
								alt=""
								className="modal-images"
							/>
							<ul>
								<li>
									<a href="#">link</a>
								</li>
							</ul>
						</div>
						<button className="display-left">&#10094;</button>
						<button className="display-right">&#10095;</button>
					</div>
				</div>
			</ModalComponent>
		</div>
	);
};

export default Work;
