// import React from 'react';
import React, { useState } from 'react';
import images from './images';
import Modal from './Modal';
import './work.css';
import WorkGrid from './WorkGrid.js';

const Work = (props) => {
	const firstImages = [];
	for (let i = 0; i < images.length; i++) {
		firstImages.push(images[i][0]);
	}
	const [slidesState, setNextSlide] = useState({
		firstImages,
	});
	return (
		<div className="work-div">
			<Modal slidesState={slidesState} setNextSlide={setNextSlide} />
			<WorkGrid slidesState={slidesState} />
		</div>
	);
};

export default Work;
