// import React from 'react';
import React, { useState } from 'react';
import images from './images';
import ModalComponent from './ModalComponent';
import UseModal from './UseModal';
import './work.css';
import ImageHover from './ImageHover';

const Work = () => {
	const [Modal, isModalOpen, setIsModalOpen] = UseModal();
	const toggleModalOpen = () => setIsModalOpen((prev) => !prev);
	const firstImages = [];
	let workIndex;
	let arrIdx = 0;
	for (let i = 0; i < images.length; i++) {
		firstImages.push(images[i][0]);
	}
	const [slidesState, setSlide] = useState(null);
	const setIndex = (index) => {
		workIndex = index;
		setSlide(images[workIndex][arrIdx]);
	};
	const forwardSlide = () => {
		setSlide(images[workIndex][arrIdx++]);
	};
	const backSlide = () => {
		setSlide(images[workIndex][arrIdx--]);
	};
	return (
		<div className="work-div">
			{firstImages.map((work, i) => {
				return (
					<div className="grid-image-div" key={`${work}_${i}`}>
						<ImageHover
							setIndex={setIndex}
							work={work}
							i={i}
							setIsModalOpen={setIsModalOpen}
							slidesState={slidesState}
						/>
					</div>
				);
			})}
			<ModalComponent
				setIsModalOpen={setIsModalOpen}
				isModalOpen={isModalOpen}
				Modal={Modal}
				toggleModalOpen={toggleModalOpen}
				size="large"
			>
				<div className="modal-container">
					<span
						className="button-display-topright"
						role="button"
						aria-label="Close Button"
						tabIndex={0}
						onClick={toggleModalOpen}
						onKeyPress={(e) => {
							if (e.key === 'Enter') {
								toggleModalOpen();
							}
						}}
					>
						&times;
					</span>
					<div className="slides-content">
						<div className="slides-div">
							<img
								src={slidesState}
								alt=""
								className="modal-images"
							/>
							<ul>
								<li>
									<a href="#">link</a>
								</li>
							</ul>
						</div>
						<button className="display-left" onClick={backSlide}>
							&#10094;
						</button>
						<button
							className="display-right"
							onClick={forwardSlide}
						>
							&#10095;
						</button>
					</div>
				</div>
			</ModalComponent>
		</div>
	);
};

export default Work;

