import React from 'react';
import ReactDOM from 'react-dom';
// import PropTypes from 'prop-types';
// import FocusTrap from 'focus-trap-react';
// import { CSSTransition, TransitionGroup } from 'react-transition-group';

const modalMount = document.getElementById('modalMount');

const Modal = (props) => {
	const { slidesState, isModalOpen, setIsModalOpen, size = 'large' } = props;

	const toggleModalOpen = () => setIsModalOpen((prev) => !prev);
	return ReactDOM.createPortal(
		<div id="modal-slides" className="modal">
			<div className="modal-content">
				<div className="modal-container">
					<span
						className="button-display-topright"
						onClick={closeButton()}
					>
						&times;
					</span>
					<div className="slides-content">
						<div className="slides-div">
							<img src={slidesState} alt="" />
							<ul>
								<li>
									<a href="#">link</a>
								</li>
							</ul>
						</div>
						<button className="display-left" onClick={bakcOne()}>
							&#10094;
						</button>
						<button
							className="display-right"
							onClick={forwardOne()}
						>
							&#10095;
						</button>
					</div>
				</div>
			</div>
		</div>,
		modalMount
	);
};

export default Modal;
