import React from 'react';

const ModalContentFunctions = (props) => {
	const {
		toggleModalOpen,
		slidesState,
		backSlide,
		forwardSlide,
		setArrIdx,
	} = props;
	// const closeButtonFunction = () => {
	// 	toggleModalOpen();
	// 	setArrIdx(0);
	// };
	return (
		<div className="modal-container">
			{/* <span
				className="button-display-topright"
				role="button"
				aria-label="Close Button"
				tabIndex={0}
				onClick={closeButtonFunction}
				onKeyPress={(e) => {
					if (e.key === 'Enter') {
						toggleModalOpen();
					}
				}}
			>
				&times;
			</span> */}
			<div className="slides-content">
				<div className="slides-div">
					<img src={slidesState} alt="" className="modal-images" />
					<ul>
						<li>
							<a href="#">link</a>
						</li>
					</ul>
				</div>
				<button className="display-left" onClick={backSlide}>
					&#10094;
				</button>
				<button className="display-right" onClick={forwardSlide}>
					&#10095;
				</button>
			</div>
		</div>
	);
};

export default ModalContentFunctions;
